import { Component, ChangeDetectionStrategy, signal } from '@angular/core';
import { RouterOutlet } from '@angular/router';

@Component({
  selector: 'pms-root',
  // standalone is default — intentionally omitted per project rules
  imports: [RouterOutlet],
  template: `
    <main>
      <header role="banner" [attr.data-count]="count()">
        <h1>PMS UI</h1>        
      </header>

      <section>
        <router-outlet></router-outlet>
      </section>

      <h1>app component works!</h1>
    </main>
  `,
  styles: [`
    header { padding: 1rem; background: var(--primary); color: white; }
    main { padding: 1rem; }
  `],
  changeDetection: ChangeDetectionStrategy.OnPush,
  host: {
    'class': 'pms-root-host'
  }
})
export class AppComponent {
  private _count = signal(0);
  readonly count = this._count.asReadonly();

  increment() {
    // use update for signals (no mutate)
    this._count.update(c => c + 1);
  }
}
